function generateValidUntil(minDays = 1, maxDays = 200) {
  return Math.floor(Math.random() * (maxDays - minDays + 1)) + minDays;
}

export const createProducts = async (prisma: PrismaClient) => {
  const productsInsertionPromise = products.map(
    async (product) =>
      await prisma.product.upsert({
        where: {
          id: product.id,
        },
        update: {
          category: {
            connect: {
              id: product.category.id,
            },
          },
          default_portion: product.default_portion,
          default_portion_type: product.portion_type as any,
          valid_for_days: generateValidUntil(),
        },
        create: {
          id: product.id,
          category: {
            connect: {
              id: product.category.id,
            },
            connectOrCreate: {
              where: {
                id: product.category.id,
              },
              create: {
                id: product.category.id,
                name: product.category.name,
              },
            },
          },
          name: product.name,
          default_portion: product.default_portion,
          default_portion_type: product.portion_type as any,
          valid_for_days: generateValidUntil(),
        },
      }),
  );

  await Promise.all(productsInsertionPromise);
};
